    public static void main(String... args) {
        //инициализация списка учеников
        students = new ArrayList<>();
        String [] records = list.split(";");
        try {
            for (String student : records) {
                students.add(new Student(student.split(":")[1], Integer.parseInt(student.split(":")[0])));
            }
        } catch(ArrayIndexOutOfBoundsException e) {
            StringWriter sw = new StringWriter();
            e.printStackTrace(new PrintWriter(sw));
            System.out.println("Ошибка инициализации списка: " + sw.toString());
            System.exit(1);
        }

        System.out.println( "\nЗадача 4. Работа со списками. Указание - создать класс \"Ученик\", реализовать для него интерфейс Comparable и воспользоваться методом sort() ");

        while(true) {
            switch(enterOper()) {
                case 1:
                       System.out.println("\nСписок учеников всех классов: ");
                       for (int i = 1; i <= students.size(); i++) {
                            System.out.println(i + " " + students.get(i - 1).toString());
                       } break;
                case 2: System.out.println("\nСоритрованный список учеников всех классов: ");
                        students.sort(Student::compareTo);
                        for (int i = 1; i <= students.size(); i++) {
                            System.out.println(i + " " + students.get(i - 1).toString());
                        } break;
                default: System.out.println("\nОперации не существует, попробуйте еще раз."); break;
            }
        }
    }

    public static int enterOper(){
        int oper;
        while(true) {
            System.out.println("\nСписок операций: ");
            Scanner in = new Scanner(System.in);
            System.out.println("\t1 - Вывести несотированный список учеников");
            System.out.println("\t2 - Вывести сотированный список учеников");
            System.out.print("\nВведите номер операции: ");
            try {
                oper = in.nextInt();
                break;
            } catch (InputMismatchException ime) {
                StringWriter sw = new StringWriter();
                ime.printStackTrace(new PrintWriter(sw));
                System.out.println("\nОшибка ввода номера операции: " + sw.toString());
            }
        }
        return oper;
    }
